import React, { Component } from 'react'
import { Container, Card, Button, InputGroup, FormControl } from 'react-bootstrap'
import { FaSearchLocation } from 'react-icons/fa'
import API from "../utils/API"
import SearchResults from "../components/SearchResults"
import { geolocated } from "react-geolocated";

class Search extends Component {
    state = { 
        food: [],
        events: [],
        query: "",
        latitude: "",
        longitude: ""     
    }
        // ==== OnClick Handlers ==== //

        handleButtonClick = () => {
            this.loadRestaurants();
        }
    // ==== API CALLS ==== //

    // ZOMATO API CALL
    loadRestaurants = () => {
        API.getRestaurants()
          .then(res => {this.setState({food: res.data})
              console.log(this.state.food)
        }
    )};

    //  loadEvents = () => {
    //      API.getEvents()
    //      .then (res=> {
    //          this.setState({
    //              events: res.data
    //          }) 
    //          console.log(this.state.events)
    //      }
    //         // setEvents(res.event)
    //     )}   
    //     // loadEvents();

    // ==== GEOLOCATION METHODS ==== //

    getLocation = async () => {
        if (navigator.geolocation) {
            navigator.geolocation.getCurrentPosition(this.showPosition);
        } else {
            console.log("Geolocation is not supported by this browser")
        }
    }

    showPosition = (position) => {
        let lat = position.coords.latitude;
        let lon = position.coords.longitude;
        return [lat, lon];
    }   

    setGeoLocation = (lat, lon) => {
        this.setState({latitude: lat, longitude: lon})
    }




render () {
    return (
        <>
            <Container>
            <Card>
                <Card.Header>
                    <h2 className="w-100 text-center mt-2">Search for local food & entertainment</h2>
                </Card.Header>
                <Card.Body>
                <InputGroup className="mb-3">
                <FormControl
                placeholder="Tacos"
                />
                <InputGroup.Append>
                <Button 
                    variant="secondary"
                    onClick={this.handleButtonClick}
                ><FaSearchLocation />
                </Button>
                </InputGroup.Append>
                </InputGroup>
                </Card.Body>

            </Card>

            <Card className="mt-2 p-3">
            {this.state.food ? (
                 <SearchResults 
                     results={this.state.food} 
                 /> 
            ) : (
            <div>
                <p>Nothing to display</p>
            </div>
            )}
            </Card>
            </Container>

        </>
    )
}}

export default Search
