import React, { Component, useState } from "react"
import { Link, useHistory } from "react-router-dom";
import firebase from "../firebase"
import"./navbar.css";
import { Navbar, Nav, NavDropdown, Form, FormControl, Button, Dropdown } from "react-bootstrap"
// Depending on the current path, this component sets the "active" className on the appropriate navigation link item
function Navigation() {

  const [isLoggedIn, setLoggedIn] = useState()
  const [userName, setUserName] = useState()

  firebase.auth().onAuthStateChanged(function(user) {
    if (user) {
      console.log("NAVBAR " + user.email)
      // User is signed in.
      setLoggedIn(true)
      setUserName(user.email)
    } else {
      // No user is signed in.
      setLoggedIn(false)
    }
  }
  );
  return (  
    <Navbar bg="dark" expand="lg">
  <Navbar.Brand href="#home"><img src="https://media.tenor.com/images/e19b269522c8b038097086e5703d192b/tenor.gif" alt="This will display an animated GIF" /> Axolotl</Navbar.Brand>
  <Navbar.Toggle aria-controls="basic-navbar-nav" />
  <Navbar.Collapse id="basic-navbar-nav">
    <Nav className="mr-auto">
      <Nav.Link href="#home">              <Link
              to="/search"
              className={
                window.location.pathname === "/Search" || window.location.pathname === "/"
                  ? "nav-link active"
                  : "nav-link"
              }
            >
              Search
            </Link></Nav.Link>
      <Nav.Link href="#link"><Link
              to="/saved"
              className={
                window.location.pathname === "/Saved" || window.location.pathname === "/"
                  ? "nav-link active"
                  : "nav-link"
              }
            >
              Saved
            </Link></Nav.Link>
      <NavDropdown title="Dropdown" id="basic-nav-dropdown" className="mr-sm-2">
        <NavDropdown.Item href="#action/3.1">Action</NavDropdown.Item>
        <NavDropdown.Item href="#action/3.2">Another action</NavDropdown.Item>
        <NavDropdown.Item href="#action/3.3">Something</NavDropdown.Item>
        <NavDropdown.Divider />
        <NavDropdown.Item href="#action/3.4">Separated link</NavDropdown.Item>
      </NavDropdown>
    </Nav>
  </Navbar.Collapse>
</Navbar>


  )}

export default Navigation;
