import axios from "axios"
// eslint-disable-next-line
export default {
    // ZOMATO API CALL
    getRestaurants(lat, lon){
            return axios.get(`https://developers.zomato.com/api/v2.1/search?query=Chelsea&lat=${lat}&lon=${lon}&count=10`,{
                headers: {"user-key": process.env.REACT_APP_ZOMATO_API_KEY}
              });

            },
   // SeatGeek API Call  
    getEvents(){
      return axios.get(`https://api.seatgeek.com/2/events?&client_id=${process.env.REACT_APP_SEATGEEK_API_KEY}`)
    },

    getFoods: function() {
      return axios.get("/api/foods");
    },
    saveFoods: function(foodData) {
      return axios.post("/api/foods", foodData)
    },
    deleteFoods(id) {
      return axios.delete("/api/foods/" + id)
    },

    getEventsDB: function() {
      return axios.get("/api/events");
    },
    saveEvents: function(eventsData) {
      return axios.post("/api/events", eventsData)
    },
    deleteEvents(id) {
      return axios.delete("/api/events/" + id)
      }
    }
