import React, { Component } from 'react'
import { Container, Card, Button, InputGroup, FormControl } from 'react-bootstrap'
import { FaSearchLocation } from 'react-icons/fa'
import API from "../utils/API"
import SearchResults from "../components/SearchResults"

class Search extends Component {
    state={
        food: [],
        events: [],
        query: "",
        latitude: "",
        longitude: ""     
    }
    componentDidMount() {
        //this.loadRestaurants();
        // this.loadEvents()
        this.getLocation().then(console.log("Got the user's location"))
    };

    // ZOMATO API CALL
    loadRestaurants = () => {
        API.getRestaurants()
          .then(res => {
              this.setState({
                  food: res.data
              })
              //console.log(this.state.food);
          }
          )};

    //  loadEvents = () => {
    //      API.getEvents()
    //      .then (res=> {
    //          this.setState({
    //              events: res.data
    //          }) 
    //          console.log(this.state.events)
    //      }
    //         // setEvents(res.event)
    //     )}   
    //     // loadEvents();

    getLocation = async () => {
        let options = {
            timeout: 5000,
            maximumAge: 0
          };

          function success(pos) {
            var crd = pos.coords;

            console.log(`Latitude : ${crd.latitude}`);
            console.log(`Longitude: ${crd.longitude}`);

            this.handleLocation()

          }

          function error(err) {
            console.warn(`ERROR(${err.code}): ${err.message}`);
          }

          navigator.geolocation.getCurrentPosition(success, error, options);

        }

        handleLocation = () => {
            console.log("Location saved")
        }

        handleButtonClick = e => {
            this.loadRestaurants();
        }


render () {
    return (
        <>
            <Container>
            <Card>
                <Card.Header>
                    <h2 className="w-100 text-center mt-2">Search for local food & entertainment</h2>
                </Card.Header>
                <Card.Body>
                <InputGroup className="mb-3">
                <FormControl
                placeholder="Tacos"
                />
                <InputGroup.Append>
                <Button 
                    variant="secondary"
                    onClick={this.handleButtonClick}
                ><FaSearchLocation />
                </Button>
                </InputGroup.Append>
                </InputGroup>
                </Card.Body>


            </Card>

            <Card className="mt-2 p-3">
                {/* <SearchResults 
                results={this.state.food} 
                /> */}
            </Card>
            </Container>

        </>
    )
}}

export default Search
