import React, {Component, useState} from 'react'
import { Card, Form, Button, Alert, Container, Modal } from 'react-bootstrap'
import { FaTrashAlt, FaPencilAlt } from "react-icons/fa"
import { IoFastFood, IoLocationSharp, IoTimeSharp, IoSaveSharp } from "react-icons/io5";

class Edit extends Component {
    state = {
        id: this.props.id,
        show: false,
        name: "",
        reservation: "",
        notes: ""
    }

    // === Handlers for our modal, close and show === //

    handleClose = () => this.setState({show: false})
    handleShow = () => this.setState({show: true})

    // === Handlers for our editable database records === //

    handleNameChange = (e) => {
        this.setState({
            name: e.target.value
        })
       //console.log(this.state.name) YUP THIS WORKS
    }

    handleReservation = (e) => {
        this.setState({
            reservation: e.target.value
        })
        // console.log(this.state.reservation) YUP THIS WORKS
    }

    handleNotes = (e) => {
        this.setState({
            notes: e.target.value
        })
        // console.log(this.state.notes) YUP THIS WORKS
    }

    handleFormSubmit = () => {
        var foodData = {
            id: this.state.id,
            name: this.state.name,
            reservation: this.state.reservation,
            notes: this.state.notes
        }
    }

render() {

    //console.log(`Currently editing record: ${this.state.id}`) YUP THIS WORKS
    return (
        <>
          <Button variant="light" onClick={this.handleShow}>
           <FaPencilAlt />
          </Button>

          <Modal show={this.state.show} onHide={this.handleClose}>
            <Modal.Header closeButton>
              <Modal.Title>Edit Record for: {this.props.name} </Modal.Title>
            </Modal.Header>
            <Modal.Body>
            <Form onSubmit={this.props.edit}>
                <Form.Group id="name">
                    <small>Restaurant Name: </small>
                    <Form.Control type="name" placeholder="New Restaraunt Name" onChange={this.handleNameChange} />
                </Form.Group>

                <Form.Group id="reservation">
                    <small>Reservation: </small>
                    <Form.Control type="name" placeholder="June 6th 2021" onChange={this.handleReservation} />
                </Form.Group>

                <Form.Group id="notes">
                    <small>Notes: </small>
                    <Form.Control type="name" placeholder="Delicious breadsticks!" onChange={this.handleNotes}/>
                </Form.Group>

                <Button className="w-100" type="submit" variant="success"><IoSaveSharp /></Button>
            </Form>
            </Modal.Body>
          </Modal>
        </>
      );
    }
}

export default Edit
