import React, {Component } from 'react'
import { Form, Button, Modal, Container } from 'react-bootstrap'
import { FaPencilAlt } from "react-icons/fa"
import { IoSaveSharp } from "react-icons/io5";
import axios from 'axios';
import { Link, useHistory } from "react-router-dom";

class Edit extends Component {
    state = {
        id: this.props.id,
        show: false,
        name: "",
        reservation: "",
        notes: "",
        foods: {}
    }

    componentDidMount() {
        axios.get("/api/foods/"+ this.props.id)
        .then(res => {
            this.setState({foods: res.data});
            console.log(this.state.foods)
            console.log(this.state.foods.id)
        })
    }

    // === Handlers for our modal, close and show === //

    handleClose = () => this.setState({show: false})
    handleShow = () => this.setState({show: true})

    // === Handlers for our editable database records === //

        handleNameChange = (e) => {
            this.setState({
                name: e.target.value
            })
        // console.log(this.state.name) 
        }

        handleReservation = (e) => {
            this.setState({
                reservation: e.target.value
            })
        //   console.log(this.state.reservation) 
        }

        handleNotes = (e) => {
            this.setState({
                notes: e.target.value
            })
           //console.log(this.state.notes) 
        }


    handleFormSubmit = () => {

       var foodData = {
            id: this.state.id,
            name: this.state.name
        }
        this.props.editFood(foodData)
        // console.log(foodData)
    }


render() {

    // console.log(`Currently editing record: ${this.state.id}`)
    return (
        <>
         <Container style={backgroundColor = white}>
         <Form onSubmit={this.handleFormSubmit}>
                <Form.Group id="name">
                    <small>Restaurant Name: </small>
                    <Form.Control type="name" placeholder="New Restaraunt Name" onChange={this.handleNameChange} />
                </Form.Group>

                <Form.Group id="reservation">
                    <small>Reservation: </small>
                    <Form.Control type="name" placeholder="June 6th 2021" onChange={this.handleReservation} />
                </Form.Group>

                <Form.Group id="notes">
                    <small>Notes: </small>
                    <Form.Control type="name" placeholder="Delicious breadsticks!" onChange={this.handleNotes}/>
                </Form.Group>

                <Button className="w-100" type="submit" variant="success"><IoSaveSharp /></Button>
            </Form>
         </Container>
        </>
      );
    }
}

export default Edit
