import React, {Component } from 'react'
import { Form, Button, Container } from 'react-bootstrap'
import { IoSaveSharp } from "react-icons/io5";
import axios from 'axios';
import { Link, useHistory, useParams, withRouter } from "react-router-dom";

class Edit extends Component {
    state = {
        show: false,
        name: "",
        reservation: "",
        notes: "",
        foods: {}
    }

    componentDidMount() {
        //console.log(this.props.location.search)
        var id = this.props.location.search;
        id = id.substring(1);
        console.log(id)
        axios.get("/api/foods/"+ id)
        .then(res => {
            this.setState({foods: res.data});
            console.log(this.state.foods)
        })
    }

    // === Handlers for our modal, close and show === //

    handleClose = () => this.setState({show: false})
    handleShow = () => this.setState({show: true})

    // === Handlers for our editable database records === //

        handleNameChange = (e) => {
            this.setState({
                name: e.target.value
            })
        // console.log(this.state.name) 
        }

        handleReservation = (e) => {
            this.setState({
                reservation: e.target.value
            })
        //   console.log(this.state.reservation) 
        }

        handleNotes = (e) => {
            this.setState({
                notes: e.target.value
            })
           //console.log(this.state.notes) 
        }


    handleChange = (e) => {
        const state = this.state.foods
        state[e.target.name] = e.target.value;
        this.setState({foods:state})
    }
    handleFormSubmit = () => {

    }


render() {

    // console.log(`Currently editing record: ${this.state.id}`)
    return (
        <>
         <Container>
         <Form onSubmit={this.handleFormSubmit}>
                <Form.Group id="name">
                    <small>Restaurant Name: </small>
                    <Form.Control type="name" placeholder="New Restaraunt Name" onChange={this.handleChange} />
                </Form.Group>

                <Form.Group id="reservation">
                    <small>Reservation: </small>
                    <Form.Control type="name" placeholder="June 6th 2021" onChange={this.handleChange} />
                </Form.Group>

                <Form.Group id="notes">
                    <small>Notes: </small>
                    <Form.Control type="name" placeholder="Delicious breadsticks!" onChange={this.handleChange}/>
                </Form.Group>

                <Button className="w-100" type="submit" variant="success"><IoSaveSharp /></Button>
            </Form>
         </Container>
        </>
      );
    }
}

export default withRouter(Edit)
