import SearchResults from "../components/SearchResults"

class Search extends Component {
    state={
        food: [],
        events: []        
    }
    componentDidMount() {
        // this.loadRestaurants();
        this.loadEvents()
    };

    // ZOMATO API CALL
    loadRestaurants = () => {
        API.getRestaurants()
          .then(res => {
              this.setState({
                  food: res.data
              })
              //console.log(this.state.food);
          }
            // setFood(res.data)
          )};
    //   loadRestaurants();


     loadEvents = () => {
